import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom';
import { Alert, Button, Link, Modal, Box, Grid } from '@mui/material';
import TextField from '@mui/material/TextField';

import api from '../../api'
import Navbar from '../../components/navbar'
import Typography from '@mui/material/Typography';
import DriverHome from '../dhome';

export default function Home() {

  const [alert, setAlert] = useState(0);
  const [alert1, setAlert1] = useState(0);
  const navigate = useNavigate();

  const messages = {
    400: ['error', "Required * fields can't be empty"],
    200: ['success', "Successful"],
    201: ['info', "Email not verified, redirecting to verify"],
    401: ['error', "Email or Password is incorrect."],
    500: ['error', "Internal Server error!"]
  }

  const onAlertClosed = () => {
    setAlert1(0);
  }
  const [userData, setUserData] = useState(null);
  const [open, setOpen] = React.useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);
  const [openRM, setOpenRM] = React.useState(false);
  const handleOpenRM = () => setOpenRM(true);
  const handleCloseRM = () => setOpenRM(false);
  const [openAB, setOpenAB] = React.useState(false);
  const handleOpenAB = () => setOpenAB(true);
  const handleCloseAB = () => setOpenAB(false);
  const [openRB, setOpenRB] = React.useState(false);
  const handleOpenRB = () => setOpenRB(true);
  const handleCloseRB = () => setOpenRB(false);
  const [openASB, setOpenASB] = React.useState(false);
  const handleOpenASB = () => setOpenASB(true);
  const handleCloseASB = () => setOpenASB(false);
  function handleSubmit(event){

    event.preventDefault();
    const data = new FormData(event.currentTarget);

    if(!data.get('name') || !data.get('phone')){
      setAlert(400);
      setTimeout(() => {
        onAlertClosed();
      }, 3000);
      return
    }

    const loginDataObj = {
      mname: data.get('name'),
      mnumber: data.get('phone')
    } 
    
    api.post('/addMember', loginDataObj, {withCredentials: true})
    .then(function(res){
      setAlert(res.status);
      if(res.status === 200){
        console.log(res.status)
        setTimeout(function(){
          onAlertClosed();
          navigate("/")
        }, 3000);
      }
      else if(res.status === 201){
        setTimeout(function(){
          onAlertClosed();
        }, 3000);
      }
    })
    .catch(function(err){
      setAlert(err.response.status);
      setTimeout(function(){
        onAlertClosed();
      }, 3000);
    }); 
  }
  function handleSubmitRM(event){

    event.preventDefault();
    const data = new FormData(event.currentTarget);

    if(!data.get('name') || !data.get('phone')){
      setAlert(400);
      setTimeout(() => {
        onAlertClosed();
      }, 3000);
      return
    }

    const loginDataObj = {
      mname: data.get('name'),
      mnumber: data.get('phone')
    } 
    
    api.post('/removeMember', loginDataObj, {withCredentials: true})
    .then(function(res){
      console.log(200)
      setAlert(res.status);
      if(res.status === 200){
        setTimeout(function(){
          onAlertClosed();
          navigate("/")
        }, 3000);
      }
      else if(res.status === 201){
        setTimeout(function(){
          onAlertClosed();
          navigate("/verifyUser", {state: { userId: res.data.userId, email: res.data.email}})
        }, 3000);
      }
    })
    .catch(function(err){
      setAlert(err.response.status);
      setTimeout(function(){
        onAlertClosed();
      }, 3000);
    }); 
  }
  function handleSubmitAB(event){

    event.preventDefault();
    const data = new FormData(event.currentTarget);

    if(!data.get('aname') || !data.get('bname')){
      setAlert(400);
      setTimeout(() => {
        onAlertClosed();
      }, 3000);
      return
    }

    const loginDataObj = {
      bname: data.get('bname'),
      aname: data.get('aname'),
    } 
    
    api.post('/addBook', loginDataObj, {withCredentials: true})
    .then(function(res){
      console.log(200)
      setAlert(res.status);
      if(res.status === 200){
        setTimeout(function(){
          onAlertClosed();
          navigate("/")
        }, 3000);
      }
      else if(res.status === 201){
        setTimeout(function(){
          onAlertClosed();
          navigate("/verifyUser", {state: { userId: res.data.userId, email: res.data.email}})
        }, 3000);
      }
    })
    .catch(function(err){
      setAlert(err.response.status);
      setTimeout(function(){
        onAlertClosed();
      }, 3000);
    }); 
  }
  function handleSubmitRB(event){

    event.preventDefault();
    const data = new FormData(event.currentTarget);

    if(!data.get('aname') || !data.get('bname')){
      setAlert(400);
      setTimeout(() => {
        onAlertClosed();
      }, 3000);
      return
    }

    const loginDataObj = {
      bname: data.get('bname'),
      aname: data.get('aname'),
    } 
    
    api.post('/removeBook', loginDataObj, {withCredentials: true})
    .then(function(res){
      console.log(200)
      setAlert(res.status);
      if(res.status === 200){
        setTimeout(function(){
          onAlertClosed();
          navigate("/")
        }, 3000);
      }
      else if(res.status === 201){
        setTimeout(function(){
          onAlertClosed();
          navigate("/verifyUser", {state: { userId: res.data.userId, email: res.data.email}})
        }, 3000);
      }
    })
    .catch(function(err){
      setAlert(err.response.status);
      setTimeout(function(){
        onAlertClosed();
      }, 3000);
    }); 
  }
  function handleSubmitAB(event){

    event.preventDefault();
    const data = new FormData(event.currentTarget);

    if(!data.get('aname') || !data.get('bname')){
      setAlert(400);
      setTimeout(() => {
        onAlertClosed();
      }, 3000);
      return
    }

    const loginDataObj = {
      bname: data.get('bname'),
      aname: data.get('aname'),
    } 
    
    api.post('/assignBook', loginDataObj, {withCredentials: true})
    .then(function(res){
      console.log(200)
      setAlert(res.status);
      if(res.status === 200){
        setTimeout(function(){
          onAlertClosed();
          navigate("/")
        }, 3000);
      }
      else if(res.status === 201){
        setTimeout(function(){
          onAlertClosed();
          navigate("/verifyUser", {state: { userId: res.data.userId, email: res.data.email}})
        }, 3000);
      }
    })
    .catch(function(err){
      setAlert(err.response.status);
      setTimeout(function(){
        onAlertClosed();
      }, 3000);
    }); 
  }
  const style = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 400,
    bgcolor: 'background.paper',
    border: '2px solid #000',
    boxShadow: 24,
    p: 4,
  };
  function onLoad(){
    api.get("/authenticate", {withCredentials:true})
    .then(res=>{
      setUserData(res.data);
    })
    .catch(err=>{
      console.log(err);
    })
  }

  useEffect(() => {
    onLoad();
  }, [])



  return (
    <>
    
    {
      (userData)?
      <>
      {
        (userData.userType === "librarian")? <DriverHome/>:<></>
      }
      </>
      :<Navbar isLoggedIn={false}></Navbar>
    }
<Modal
  open={open}
  onClose={handleClose}
  aria-labelledby="modal-modal-title"
  aria-describedby="modal-modal-description"
>
  <Box component="form" noValidate onSubmit={handleSubmit} sx={style}>
  {
      (alert)
      ?
      <Alert severity={messages[alert][0]} variant='standard'>{messages[alert][1]}</Alert>
      :
      <></>
    }
  <Grid container spacing={2}>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  id="name"
                  label="Name"
                  name="name"
                  autoComplete="name"
                  />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  name="phone"
                  label="Mobile Number"
                  type="number"
                  id="phone"
                  autoComplete="phone"
                  />
              </Grid>
            </Grid>
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
              >
              Submit
            </Button>
  </Box>
</Modal>
    <Button
              type="submit"
              
              variant="contained"
              sx={{margin:2, mt: 3, mb: 2 }}
              onClick={handleOpen}
              >
              Add Member
            </Button>
            <Modal
              open={openRM}
              onClose={handleCloseRM}
              aria-labelledby="modal-modal-title"
              aria-describedby="modal-modal-description"
            >
  <Box component="form" noValidate onSubmit={handleSubmitRM} sx={style}>
  {
      (alert)
      ?
      <Alert severity={messages[alert][0]} variant='standard'>{messages[alert][1]}</Alert>
      :
      <></>
    }
  <Grid container spacing={2}>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  id="name"
                  label="Name"
                  name="name"
                  autoComplete="name"
                  />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  name="phone"
                  label="Mobile Number"
                  type="number"
                  id="phone"
                  autoComplete="phone"
                  />
              </Grid>
            </Grid>
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
              >
              Submit
            </Button>
          </Box>
        </Modal>
    <Button
              type="submit"
              onClick={handleOpenRM}
              variant="contained"
              sx={{margin:2, mt: 3, mb: 2 }}
              >
              Remove Member
            </Button>
  <Modal
    open={openAB}
    onClose={handleCloseAB}
    aria-labelledby="modal-modal-title"
    aria-describedby="modal-modal-description"
  >
  <Box component="form" noValidate onSubmit={handleSubmitAB} sx={style}>
  {
      (alert)
      ?
      <Alert severity={messages[alert][0]} variant='standard'>{messages[alert][1]}</Alert>
      :
      <></>
    }
  <Grid container spacing={2}>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  id="bname"
                  label="Book Name"
                  name="bname"
                  autoComplete="name"
                  />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  name="aname"
                  label="Author Name"
                  type="name"
                  id="aname"
                  autoComplete="phone"
                  />
              </Grid>
            </Grid>
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
              >
              Submit
            </Button>
          </Box>
        </Modal>
    
    <Button
              type="submit"
              onClick={handleOpenAB}
              variant="contained"
              sx={{margin:2, mt: 3, mb: 2 }}
              >
              Add Book
            </Button>
            <Modal
    open={openRB}
    onClose={handleCloseRB}
    aria-labelledby="modal-modal-title"
    aria-describedby="modal-modal-description"
  >
  <Box component="form" noValidate onSubmit={handleSubmitRB} sx={style}>
  {
      (alert)
      ?
      <Alert severity={messages[alert][0]} variant='standard'>{messages[alert][1]}</Alert>
      :
      <></>
    }
  <Grid container spacing={2}>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  id="bname"
                  label="Book Name"
                  name="bname"
                  autoComplete="name"
                  />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  name="aname"
                  label="Author Name"
                  type="name"
                  id="aname"
                  autoComplete="phone"
                  />
              </Grid>
            </Grid>
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
              >
              Submit
            </Button>
          </Box>
        </Modal>
  
    <Button
              type="submit"
              onClick={handleOpenRB}
              variant="contained"
              sx={{margin:2, mt: 3, mb: 2 }}
              >
              Remove Book
            </Button>
            <Modal
    open={openASB}
    onClose={handleCloseASB}
    aria-labelledby="modal-modal-title"
    aria-describedby="modal-modal-description"
  >
  <Box component="form" noValidate onSubmit={handleSubmitASB} sx={style}>
  {
      (alert)
      ?
      <Alert severity={messages[alert][0]} variant='standard'>{messages[alert][1]}</Alert>
      :
      <></>
    }
  <Grid container spacing={2}>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  id="bname"
                  label="Book Name"
                  name="bname"
                  autoComplete="name"
                  />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  name="aname"
                  label="Author Name"
                  type="name"
                  id="aname"
                  autoComplete="phone"
                  />
              </Grid>
            </Grid>
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
              >
              Submit
            </Button>
          </Box>
        </Modal>
    <Button
              type="submit"
              onClick={handleOpenASB}
              variant="contained"
              sx={{margin:2, mt: 3, mb: 2 }}
              >
              Assign Book
            </Button>
    <Button
              type="submit"
             
              variant="contained"
              sx={{margin:2, mt: 3, mb: 2 }}
              >
              Members List
            </Button>
    <Button
              type="submit"
            
              variant="contained"
              sx={{ margin:2,mt: 3, mb: 2 }}
              >
              Books List
            </Button>
    <Button
              type="submit"
              
              variant="contained"
              sx={{ margin:2,mt: 3, mb: 2 }}
              >
              Search Book
            </Button>
    </>
  )
}
