import React, { useEffect, useState } from 'react'
import { Alert, Button, Link, Modal, Box, Grid } from '@mui/material';
import api from '../../api'
import Navbar from '../../components/navbar'
import Typography from '@mui/material/Typography';
// import DriverHome from '../dhome';
// import OwnerHome from '../ohome';

export default function Home() {

  const [alert, setAlert] = useState(0);
  const [userData, setUserData] = useState(null);
  const [open, setOpen] = React.useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);
  const messages = {
    403: ['error', 'You are not allowed to access this page'],
  }
  const style = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 400,
    bgcolor: 'background.paper',
    border: '2px solid #000',
    boxShadow: 24,
    p: 4,
  };
  function onLoad(){
    api.get("/authenticate", {withCredentials:true})
    .then(res=>{
      setUserData(res.data);
    })
    .catch(err=>{
      console.log(err);
    })
  }

  useEffect(() => {
    onLoad();
  }, [])



  return (
    <>
    {
      (alert)
      ?
      <Alert severity={messages[alert][0]} variant='standard'>{messages[alert][1]}</Alert>
      :
      <></>
    }
    <Navbar isLoggedIn={false}></Navbar>
<Modal
  open={open}
  onClose={handleClose}
  aria-labelledby="modal-modal-title"
  aria-describedby="modal-modal-description"
>
  <Box sx={style}>
  <Grid container spacing={2}>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  id="email"
                  label="Email Address"
                  name="email"
                  autoComplete="email"
                  />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  name="password"
                  label="Password"
                  type="password"
                  id="password"
                  autoComplete="new-password"
                  />
              </Grid>
            </Grid>
            <Button
              type="submit"
              fullWidth
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
              >
              Log In
            </Button>

  </Box>
</Modal>
    <Button
              type="submit"
              
              variant="contained"
              sx={{margin:2, mt: 3, mb: 2 }}
              onClick={handleOpen}
              >
              Add Member
            </Button>
    <Button
              type="submit"
              
              variant="contained"
              sx={{margin:2, mt: 3, mb: 2 }}
              >
              Remove Member
            </Button>
    <Button
              type="submit"
              
              variant="contained"
              sx={{margin:2, mt: 3, mb: 2 }}
              >
              Add Book
            </Button>
    <Button
              type="submit"
              
              variant="contained"
              sx={{margin:2, mt: 3, mb: 2 }}
              >
              Remove Book
            </Button>
    <Button
              type="submit"
             
              variant="contained"
              sx={{margin:2, mt: 3, mb: 2 }}
              >
              Assign Book
            </Button>
    <Button
              type="submit"
             
              variant="contained"
              sx={{margin:2, mt: 3, mb: 2 }}
              >
              Members List
            </Button>
    <Button
              type="submit"
            
              variant="contained"
              sx={{ margin:2,mt: 3, mb: 2 }}
              >
              Books List
            </Button>
    <Button
              type="submit"
              
              variant="contained"
              sx={{ margin:2,mt: 3, mb: 2 }}
              >
              Search Book
            </Button>
    </>
  )
}
