import React, { useEffect, useState } from 'react'
import { Alert, Button } from '@mui/material';
import api from '../../api'
import Navbar from '../../components/navbar'
// import DriverHome from '../dhome';
// import OwnerHome from '../ohome';

export default function Home() {

  const [alert, setAlert] = useState(0);
  const [userData, setUserData] = useState(null);

  const messages = {
    403: ['error', 'You are not allowed to access this page'],
  }

  function onLoad(){
    api.get("/authenticate", {withCredentials:true})
    .then(res=>{
      setUserData(res.data);
    })
    .catch(err=>{
      console.log(err);
    })
  }

  useEffect(() => {
    onLoad();
  }, [])
  

  return (
    <>
    {
      (alert)
      ?
      <Alert severity={messages[alert][0]} variant='standard'>{messages[alert][1]}</Alert>
      :
      <></>
    }
    <Navbar isLoggedIn={false}></Navbar>
    <Button
              type="submit"
              
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
              >
              Add Member
            </Button>
    <Button
              type="submit"
              
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
              >
              Remove Member
            </Button>
    <Button
              type="submit"
              
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
              >
              Add Book
            </Button>
    <Button
              type="submit"
              
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
              >
              Remove Book
            </Button>
    <Button
              type="submit"
             
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
              >
              Assign Book
            </Button>
    <Button
              type="submit"
             
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
              >
              Members List
            </Button>
    <Button
              type="submit"
            
              variant="contained"
              sx={{ mt: 3, mb: 2 }}
              >
              Books List
            </Button>
    <Button
              type="submit"
              
              variant="contained"
              sx={{ margin:2,mt: 3, mb: 2 }}
              >
              Search Book
            </Button>
    </>
  )
}
